import React from "react";
import {Container, Card, CardImg} from "react-bootstrap";
import {FontAwesomeIcon} from '@fortawesome/react-fontawesome'
import {faCoffee} from '@fortawesome/free-solid-svg-icons'

import {useWeathers} from "../context/WeatherForecastContext";
import CardHeader from "react-bootstrap/CardHeader";

function WeatherForecast() {
    const {weathers} = useWeathers();

    return (
        <div>
            <Container>
                {
                    Array.isArray(weathers) ?
                        (
                            weathers.map((item) =>
                                <Card style={{width: '19rem'}} key={item.id}>
                                    <Card.Body style={{textAlign: 'center'}}>
                                        <CardImg>
                                            <img src={`http://openweathermap.org/img/wn/${item.icon}@2x.png`} />
                                        </CardImg>
                                        <Card.Title>
                                            {item.description}
                                        </Card.Title>
                                        <CardHeader>
                                            {item.description}
                                        </CardHeader>
                                    </Card.Body>
                                </Card>
                            )
                        ) : ("Kay覺t Bulunamad覺...")
                }

            </Container>
        </div>
    )

}

export default (WeatherForecast);
