import {createContext, useContext, useEffect, useState} from "react";
import axios from "axios";

const WeatherForecastContext = createContext();

export const WeatherForecastProvider = ({children}) => {

    const [city, setCity] = useState(localStorage.getItem("city" || null));
    const [weathers, setWeathers] = useState([]);

    useEffect(() => {

        async function getAllWeather() {
            const {data} = await axios.get(`http://api.openweathermap.org/data/3.0/onecall/timemachine?lat=39.099724&lon=-94.578331&dt=1643803200&appid=321de66277f502061ee944720da49089`);
            setWeathers(data);
        }
        getAllWeather();

    }, [city]);

    const values = {
        weathers,
        setWeathers
    }

    return (
        <WeatherForecastContext.Provider value={values}>{children} </WeatherForecastContext.Provider>
    );

};

export const useWeathers = () => useContext(WeatherForecastContext);
