import {createContext, useState, useEffect, useContext} from "react";
import axios from "axios";

const CitiesContext = createContext();

export const CitiesProvider = ({children}) => {

    const [cities, setCities] = useState([]);

    useEffect(() => {
        async function fetchData() {
            const { data } = await fetch(`file:///Users/taylantezel/Desktop/ReactDerslerim/patika-react-odev-3/weather-forecast/src/data/cities_of_turkey.json`)
                .then(response => response.json())

            setCities(data);
        }
        fetchData();
    }, []);

    const values ={
        cities,
        setCities
    }

    return(
        <CitiesContext.Provider value={values}>{children}</CitiesContext.Provider>
    );
};

export const useCities = () =>useContext(CitiesContext);
