    const [cityId, setCityId] = useState(localStorage.getItem("cityId" || null));
    const {city, setCity} = useState({});

    useEffect(() =>{
        localStorage.setItem("cityId",cityId)
    },[cityId]);

    const values = {

    }

    useEffect(() => {
        async function getAllCity() {
            const {data} = await axios.get(`http://localhost:3000/cities?id=${cityId}`)
                .then(res => {
                    setCity(data);
                });
        }
        getAllCity();

    }, [cityId]);


    return (
        <WeatherForecastContext.Provider value={values}>{children} </WeatherForecastContext.Provider>
    );

};

export const useWeathers = () => useContext(WeatherForecastContext);
